<?phpnamespace App;use Illuminate\Database\Eloquent\Model;class Customer extends Model{    private $name;    private $nameOfShop;    private $teleNo;    private $orderTransactions = array();    private $customerNo;    function __construct($name, $telNo) {        $this->name = $name;        $this->teleNo = $telNo;    }    public function getName() {        return $this->name;    }    public function getTelNo() {        return $this->telNo;    }    public function getShopName() {        return $this->nameOfShop;    }    public function setTelNo($telNo) {        $this->teleNo = $telNo;    }    public function setName($name) {        $this->name = $name;    }    public function setShopName($name) {        $this->nameOfShop = $name;    }}